import { Router, useRouter } from "next/router";
import { useRef, useEffect } from "react";

const ProductD = ({ product }) => {
  // const router = useRouter();
  // if (router.isFallback) {
  //   return <h1>Loading</h1>;
  // }
  const modalRef = useRef(null);
  useEffect(() => {
    M.Modal.init(modalRef.current);
  }, []);
  const getModal = () => {
    return (
      <div id="modal1" class="modal" ref={modalRef}>
        <div className="modal-content">
          <h4>{product.name}</h4>
          <p>Are you sure you want to delete</p>
        </div>
        <div className="modal-footer">
          <button
            className="btn waves-effect waves-light #c62828 red darken-3"
            onClick={() => deleteproduct()}
          >
            Yes
          </button>
          <button className="btn waves-effect waves-light #c62828 blue darken-3">
            Cancel
          </button>
        </div>
      </div>
    );
  };
  const deleteproduct = async () => {
    const a = await fetch(
      `http://localhost:3000/api/productsdetail/${product.id}`,
      {
        method: "DELETE",
      }
    );
    await res.json();
    router.push("/");
  };
  return (
    <div className="container center-align">
      <h3>{product.name}</h3>
      <img src={product.mediaUrl} style={{ width: "30%" }} />
      <h5>Rs {product.price}</h5>
      <input
        type="number"
        style={{ width: "400px", margin: "10px" }}
        min="1"
        placeholder="Qunatity"
      ></input>
      <button className="btn waves-effect waves-light #1565c0 blue darken-3">
        Add
        <i className="material-icons right">add</i>
      </button>
      <p className="left-align">{product.description}</p>
      <button
        data-target="modal1"
        className="btn modal-trigger waves-effect waves-light #c62828 red darken-3"
      >
        Delete
        <i className="material-icons right">delete</i>
      </button>
      {getModal()}
    </div>
  );
};

export default ProductD;

