import Stripe from "stripe";
import { v4 as uuidV4 } from "uuid";
import Cart from "../../models/Cart";
import jwt from "jsonwebtoken";
// import Order from "../../models/Order";
import initDb from "../../helpers/initDB";

initDb();

const stripe = Stripe(process.env.STRIPE_SECRET);
export default async (req, res) => {
  const { paymentinfo } = req.body;
  const { authorization } = req.headers;
  if (!authorization) {
    return res.status(401).json({ error: "you must logged in" });
  }

  try {
    const { userid } = jwt.verify(authorization, "fxfgcgvhgvh");
    const cart = await Cart.findOne({ user: userid }).populate("products.prod");
    let price = 0;
    cart.prods.forEach((item) => {
      price = price + item.quantity * item.prod.price;
    });
    const prevCustomer = await stripe.customers.list({
      email: paymentinfo.email,
    });
    const isExistingCustomer = prevCustomer.data.length > 0;
    let newCustomer;
    if (!isExistingCustomer) {
      newCustomer = await stripe.customers.create({
        email: paymentinfo.email,
        source: paymentinfo.id,
      });
    }

    await stripe.charges.create(
      {
        currency: "INR",
        amount: price * 100,
        receipt_email: paymentinfo.email,
        customer: isExistingCustomer ? prevCustomer.data[0].id : newCustomer.id,
        description: `you purchased a prod | ${paymentinfo.email}`,
      },
      {
        idempotencyKey: uuidV4(),
      }
    );
    // await new Order({
    //   user: userId,
    //   email: paymentinfo.email,
    //   total: price,
    //   prods: cart.prods,
    // }).save();
    // await Cart.findOneAndUpdate({ _id: cart._id }, { $set: { prods: [] } });
    res.status(200).json({ message: "payment was successful" });
  } catch (err) {
    console.log(err);
    return res.status(401).json({ error: "error processing payment" });
  }
};
