import { parseCookies } from "nookies";
import { useRouter } from "next/router";
import cookie, { remove } from "js-cookie";
import { useState } from "react";
import Link from "next/link";

const Cart = ({ error, p }) => {
  const [pr, setprod] = useState(p.products);
  const { token } = parseCookies();
  console.log(p);
  console.log(p.products.length);
  const bb = p.products;
  const router = useRouter();
  if (!token) {
    return (
      <div className="center-align">
        <h3>Pls Login to View Your Card</h3>
        <Link href="/login">
          <a>
            <button className="btn"> Login</button>
          </a>
        </Link>
      </div>
    );
  }
  const remove = async (pid) => {
    const res = await fetch(`http://localhost:3000/api/cart`, {
      method: "DELETE",
      headers: {
        Authorization: token,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        productid: pid,
      }),
    });
    const res2 = await res.json();
    console.log(res2);
  };
  const CardItem = () => {
    return (
      <>
        {pr.map((item) => {
          return (
            <div style={{ display: "flex", margin: "20px" }}>
              <img src={item.prod.mediaUrl} style={{ width: "30%" }}></img>
              <div style={{ marginLeft: "20px" }}>
                <h6>{item.prod.name}</h6>
                <h6>
                  {item.quantity}x{item.prod.price}
                </h6>
                <button
                  className="btn red"
                  onClick={() => remove(item.prod._id)}
                ></button>
              </div>
            </div>
          );
        })}
      </>
    );
  };
  if (error) {
    M.toast({ html: error, classes: "red" });
    cookie.remove("user");
    cookie.remove("token");
    router.push("/login");
  }
  return (
    <div className="container">
      <CardItem />
    </div>
  );
};

export async function getServerSideProps(ctx) {
  const { token } = parseCookies(ctx);
  if (!token) {
    return {
      props: { error: "pls Login" },
    };
  }
  const res = await fetch(`http://localhost:3000/api/cart`, {
    headers: {
      Authorization: token,
    },
  });
  const res2 = await res.json();
  console.log("p===>" + res2);
  if (res2.error) {
    return {
      props: { error: res2.error },
    };
  }

  return {
    props: {
      p: res2,
    },
  };
}
export default Cart;
